import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";

import { getAllPokemon } from '../../services/PokemonService'
import { getModel } from '../../services/ModelService'
import logoImg from "../../assets/pocketdexlogo.png";

import Logo from "../../components/Logo/Logo";
import PokemonBox from "../../components/PokemonBox/PokemonBox";
import PokemonComparisonBox from "../../components/PokemonComparisonBox/PokemonComparisonBox";
import * as tf from '@tensorflow/tfjs'

const HomeScreen = () => {
  const [pokemonList, setPokemonList] = useState({ id: 0, pokemonName: '' });
  const [filterShow, setFilterShow] = useState(false);
  const [pokemonUploadImage, setPokemonUploadImage] = useState("");
  const [model, setModel] = useState(false);
  const [predictionList, setPredictionList] = useState(false);
  const canvasRef = useRef(null);
  const [imageURL, setImageURl] = useState(null);


  const TARGET_CLASSES = {
    0: "Bulbasaur",
    1: "Charmander",
    2: "Squirtle"
  };
  // Similar to component will mount, in order to prevent it from rendering beforehand, 
  // put it in the fetchPokemonList async function above but with current implementation 
  //we can have it display data as soon
  useEffect(() => {
    let mounted = true;
    getAllPokemon()
      .then(items => {
        if (mounted) {
          console.log(items)
          setPokemonList(items)
        }
      })
    getModel()
      .then(items => {
        if (mounted) {
          console.log( "Model loaded." );
          setModel(items)
          console.log(items)
        }
    })



    return () => mounted = false;
  }, [])
  console.log(`Is filter showing:  ${filterShow}`);

  const hiddenFileInput = React.useRef(null);

  const handleClick = event => {
    hiddenFileInput.current.click();
  };

  const handleImgUpload = (event) => {
    console.log(event.target.files[0].src);
    setPokemonUploadImage(event.target.files[0]);
    setPredictionList([]);
  };

  const drawImageOnCanvas = (image, canvas, ctx) => {
    const naturalWidth = image.naturalWidth;
    const naturalHeight = image.naturalHeight;
    canvas.width = image.width;
    canvas.height = image.height;

    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    const isLandscape = naturalWidth > naturalHeight;
    ctx.drawImage(
      image,
      isLandscape ? (naturalWidth - naturalHeight) / 2 : 0,
      isLandscape ? 0 : (naturalHeight - naturalWidth) / 2,
      isLandscape ? naturalHeight : naturalWidth,
      isLandscape ? naturalHeight : naturalWidth,
      0,
      0,
      ctx.canvas.width,
      ctx.canvas.height
    );
  };

  const onImageChange = async ({ target }) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    drawImageOnCanvas(target, canvas, ctx);

    const predictions = await model.classify(canvas, 5);
    console.log(predictions)
    setPredictionList(predictions);
  };

  const handlePredictClick = async event => {
    if (!model) { alert("The model must be loaded first"); return; }
    if (!pokemonUploadImage) { alert("Please select an image first"); return; }

    var img = new Image();
    img.src = URL.createObjectURL(pokemonUploadImage)

    img.onload = () => {
    // Pre-process the image
    console.log( "Loading image..." );
    let tensor = tf.browser.fromPixels(img)
      .resizeNearestNeighbor([224, 224]) // change the image size
      .expandDims()
      .toFloat()
      .reverse(-1); // RGB -> BGR
    let predictions = model.predict(tensor).data();
    console.log(predictions[0]);
    console.log('Hey its done');
    let top5 = Array.from(predictions)
      .map(function (p, i) { // this is Array.map
        return {
          probability: p,
          className: TARGET_CLASSES[i] // we are selecting the value from the obj
        };
      }).sort(function (a, b) {
        return b.probability - a.probability;
      }).slice(0, 2);

    setPredictionList([]);
    top5.forEach(function (p) {
      setPredictionList(predictionList.append(`<li>${p.className}: ${p.probability.toFixed(6)}</li>`));
      });
    }
  };
  const handleUploadChange = ({ target }) => {
    setImageURl(URL.createObjectURL(target.files[0]));
  };
  const renderInput = () => (
    <input
      type="file"
      onChange={handleUploadChange}
      accept="image/x-png,image/gif,image/jpeg"
    />
  );

  const renderPreview = () => (
    <canvas className="classified-image" ref={canvasRef}>
      <img alt="preview" onLoad={onImageChange} src={imageURL} />
    </canvas>
  );



  return (
    <Background>
            {
              // Show loading if model hasnt loaded yet
              !model && 
              <div>LOADING!</div>
            }
            {
              model &&    
      <Modal>
          <PokemonListTopCard>
            <PokemonListLogoCard>
            {
              // Show logo image if we haven't uploaded an image to compare yet
              !pokemonUploadImage &&
              <Logo imageUrl={logoImg}></Logo>
            }
            {
              pokemonUploadImage &&
              <ComparisonBoxCtn>
              <PokemonComparisonBox id={"myimg"} pokemonImgUrl={pokemonUploadImage}></PokemonComparisonBox>
              <PokemonComparisonBox pokemonS3Url={`https://pocket-dex-bucket.s3.us-east-2.amazonaws.com/${pokemonList[0]?.keyName}.png`}></PokemonComparisonBox>
              </ComparisonBoxCtn>
            }


            </PokemonListLogoCard>
            <PokemonListSearchCard>
              <FilterButton onClick={
                () => setFilterShow(!filterShow)
              }>Filter</FilterButton>
              <FilterButton onClick={
                handlePredictClick
              }>Predict</FilterButton>
              <UploadButton
              onClick={handleClick}>Search via Upload</UploadButton>
              <input type="file"
             ref={hiddenFileInput}
             onChange={handleImgUpload}
             style={{display:'none'}} 
             /> 
            </PokemonListSearchCard>
          </PokemonListTopCard>
          {
              // Show filter selection if we click filter button
              filterShow &&
              <FilterTableCard>
                <FilterTableSubsectionButton>
                  Filter Eyes
                </FilterTableSubsectionButton>
                <FilterTableSubsectionButton>
                  Filter Legs
                </FilterTableSubsectionButton>
                <FilterTableSubsectionButton>
                  Filter Arms
                </FilterTableSubsectionButton>
                <FilterTableSubsectionButton>
                  Filter Head
                </FilterTableSubsectionButton>
              </FilterTableCard>
            }
          <PokemonListMainCard>
            <PokemonBox pokemonName={pokemonList[0]?.pokemonName} pokemonNumber={pokemonList[0]?.id} pokemonS3Url={pokemonList[0]?.keyName}></PokemonBox>
            <PokemonBox pokemonName={pokemonList[1]?.pokemonName} pokemonNumber={pokemonList[1]?.id} pokemonS3Url={pokemonList[1]?.keyName}></PokemonBox>
            <PokemonBox pokemonName={pokemonList[2]?.pokemonName} pokemonNumber={pokemonList[2]?.id} pokemonS3Url={pokemonList[2]?.keyName}></PokemonBox>
           </PokemonListMainCard>
      </Modal>
       }
      <BottomHeader></BottomHeader>

    </Background>
  );
}

export default HomeScreen;

//Temp styler for containing comparison of images
const ComparisonBoxCtn = styled.div`
  flex: 1;
  display:flex;
