import React, {useState, useEffect} from "react";
import styled from "styled-components";

import { getAllPokemon } from '../../services/PokemonService'


const HomeScreen = () => {
  const [pokemonList, setPokemonList] = useState({ id: 0, pokemonName: '' });

  // Similar to component will mount, in order to prevent it from rendering beforehand, 
  // put it in the fetchPokemonList async function above but with current implementation 
  //we can have it display data as soon
  useEffect(() => {
    let mounted = true;
    getAllPokemon()
      .then(items => {
        if(mounted) {
          console.log(items)
          setPokemonList(items)
        }
      })
    return () => mounted = false;
  }, [])


    return (
      <Background>
        <DivOne>        
          <DivTwo>  
            <p>{pokemonList[0]?.pokemonName}</p>
          </DivTwo>
        </DivOne>
      </Background>
    );
  }

  export default HomeScreen;


const Background = styled.div`
  width: 100%;
  height: 100%;
  position: fixed;
  align-items: center;
  background-size: cover;
  top: 0;
  left: 0;
  background-color: red;
  display: flex;
  flex-direction: column;
`
const DivOne = styled.div`
  width: 70%;
  height: 100%;
  position: center;
  background-size: cover;
  top: 0;
  left: 0;
  background-color: blue;
  display: flex;
  flex-direction: column;
`

const DivTwo = styled.div`
  width: 10%;
  height: 100%;
  background-size: cover;
  top: 0;
  left: 0;
  background-color: green;
  display: flex;
  flex-direction: row;
`
